import React from 'react';
import axios from 'axios';
function ImageUploader({ setUploadedImage }) {
  const handleImageUpload = async (e) => {
    const file = e.target.files[0];
    if (file) {
      console.log(file)
      const formData = new FormData();
      formData.append('image', file);
      try {
        const response = await axios.post('/api/images/upload', formData, {
          headers: {
            'Content-Type': 'multipart/form-data'
          }
        });
        if(document.getElementById("bright") && document.getElementById("contrast")){
          const bright_element = document.getElementById("bright");
          const contrast_element = document.getElementById("contrast");
          bright_element.value = 100;
          contrast_element.value = 100;
          console.log("reset value")
        }
        setUploadedImage(response.data.filePath);
        console.log('Edited image response:', response.data);  // 添加日志
      } catch (error) {
        console.error('Failed to upload image', error);
      }
    }
  };

  return (
    <div>
      {/* 上傳圖片，onChange:當輸入改變時觸發handleImageUpload函式 */}
      <input type="file" accept="image/*" onChange={handleImageUpload}  />
    </div>
  );
}

export default ImageUploader;
